const validate = values => {
    const errors = {}
    if (!values.username) {
        errors.username = 'Required'
    }
    if (!values.orgName) {
        errors.orgName = 'Required'
    }
    if (!values.orgType) {
        errors.orgType = 'Required'
    }
    if (!values.selectRole) {
        errors.selectRole = 'Required'
    }

    return errors
}
