/**
 * Obtain default value
 * @returns {Object}
 */
export function getDefaultValue(viewerWidth, viewerHeight, SVGWidth, SVGHeight, scaleFactorMin, scaleFactorMax) {
  return set({}, {
    ...identity(),
    version: 2,
    mode: MODE_IDLE,
    focus: false,
    pinchPointDistance: null,
    prePinchMode: null,
    viewerWidth,
    viewerHeight,
    SVGWidth,
    SVGHeight,
    scaleFactorMin,
    scaleFactorMax,
    startX: null,
    startY: null,
    endX: null,
    endY: null,
    miniatureOpen: true,
    lastAction: null,
  });
}

/**
 * Change value
 * @param value
 * @param change
 * @param action
 * @returns {Object}
 */
export function set(value, change, action = null) {
  value = Object.assign({}, value, change, {lastAction: action});
  return Object.freeze(value);
}

/**
 * value valid check
 * @param value
